import React, { useState } from 'react'
import axios from 'axios'

function App() {
  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  //const url = `http://api.weatherapi.com/v1/current.json?key=28432a34394a4f24aea202609232006&q=${location}`
  const url = `http://localhost:3002/temp?city=${location}`

  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        downloadImage(urlImg)
        console.log(response.data)
      })
      setLocation('')
    }
  }

  const $btn = document.getElementById('downloadImage')
  const urlImg = data.current.condition.code

  const fetchImage = async urlImg => {
    const response = await fetch(urlImg)
    const blob = await response.blob()

    return blob
  }

  const downloadImage = async urlImg => {
    const imageBlob = await fetchImage(urlImg)
    const imageBase64 = URL.createObjectURL(imageBlob)

    console.log({imageBase64})

    const a = document.createElement('a')
    a.style.setProperty('display', 'none')
    document.body.appendChild(a)
    a.download = urlImg.replace(/^.*[\\\/]/, '')
    a.href = imageBase64
    a.click()
    a.remove()
  }



  return (
    <div className="app">
      <div className="search">
        <input
          value={location}
          onChange={event => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder='Enter Location'
          type="text" />
      </div>
      <div className="container">
        <div className="top">
          <div className="location">
            <p>{data.location ? data.location.name : data.name}</p>{data.location ? data.location.region : data.name}, <b>{data.location ? data.location.country : data.name}</b>
          </div>

          <div className="temp">
            {data.current ? <h1>{data.current.temp_f}°F</h1> : null}
          </div>
          <div className="description">
            {data.weather ? <p>{data.weather[0].main}</p> : null}
          </div>
        </div>

        {data.name !== undefined &&
          <div className="bottom">
            <div className="feels">
              {data.main ? <p className='bold'>{data.main.feels_like.toFixed()}°F</p> : null}
              <p>Feels Like</p>
            </div>
            <div className="humidity">
              {data.main ? <p className='bold'>{data.main.humidity}%</p> : null}
              <p>Humidity</p>
            </div>
            <div className="wind">
              {data.wind ? <p className='bold'>{data.wind.speed.toFixed()} MPH</p> : null}
              <p>Wind Speed</p>
            </div>
          </div>
        }



      </div>
    </div>
  );
}

export default App;
